const sliderLine=document.querySelector(".slider-line"),prevButton=document.querySelector(".button-prev"),nextButton=document.querySelector(".button-next"),dots=document.querySelectorAll(".dot");let position=0,dotIndex=0;const nextSlide=()=>{position<620*(dots.length-1)?(position+=620,dotIndex++):(position=0,dotIndex=0),sliderLine.style.left=-position+"px",thisSlide(dotIndex)},prevSlide=()=>{0<position?(position-=620,dotIndex--):(position=620*(dots.length-1),dotIndex=dots.length-1),sliderLine.style.left=-position+"px",thisSlide(dotIndex)},thisSlide=a=>{for(let b of dots)b.classList.remove("active");dots[a].classList.add("active")};nextButton.addEventListener("click",nextSlide),prevButton.addEventListener("click",prevSlide),dots.forEach((a,b)=>{a.addEventListener("click",()=>{position=620*b,sliderLine.style.left=-position+"px",dotIndex=b,thisSlide(dotIndex)})});